mapper.select:
select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.create_time, s.update_time, s.create_user, s.update_user, c.name categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat("%",#{name},"%")
and s.category_id=#{categoryId}
and s.status=#{status}
